echo "Starting deployment script run" && sudo apt-get update && curl -sL https://deb.nodesource.com/setup_14.x | sudo -E bash - && sudo apt-get install -y nodejs && sudo apt-get install -y nginx && sudo rm -f /etc/nginx/sites-enabled/default && sudo chmod 0755 ~/dist && sudo cp config /etc/nginx/sites-available/ && sudo ln -sf /etc/nginx/sites-available/config /etc/nginx/sites-enabled/config && sudo service nginx restart && echo "End of script run"
